function calculateDemographicScore(record) {
  // Data-driven scoring function generated from SHAP importance analysis
  
  let totalScore = 0;
  let componentCount = 0;
  

  // Component: mp10128a_b_p (weight: 1.000)
  const mp10128a_b_p_raw = Number(record.MP10128A_B_P) || Number(record.mp10128a_b_p) || 0;
  let mp10128a_b_p_normalized = 0;
  
  if (mp10128a_b_p_raw > 0) {
    // Normalization: min_max_scale
    mp10128a_b_p_normalized = Math.min((mp10128a_b_p_raw - 0) / (100) * 100, 100);
    
    // Transformation: direct normalization
    totalScore += 1.000 * mp10128a_b_p_normalized;

    componentCount++;
  }

  
  // Ensure score is in 0-100 range
  const finalScore = Math.max(0, Math.min(100, totalScore));
  
  return Math.round(finalScore * 100) / 100;
}



